function c = mult_poly22(a, b)
    c = zeros(1, 1, 15);
    c(1) = a(:, :, 1)*b(:, :, 1);                                                                                     %x^4
    c(2) = a(:, :, 1)*b(:, :, 2) + a(:, :, 2)*b(:, :, 1);                                                             %x^3*y
    c(3) = a(:, :, 1)*b(:, :, 3) + a(:, :, 2)*b(:, :, 2) + a(:, :, 3)*b(:, :, 1);                                     %x^2*y^2
    c(4) = a(:, :, 2)*b(:, :, 3) + a(:, :, 3)*b(:, :, 2);                                                             %x*y^3
    c(5) = a(:, :, 3)*b(:, :, 3);                                                                                     %y^4
    c(6) = a(:, :, 1)*b(:, :, 4) + a(:, :, 4)*b(:, :, 1);                                                             %x^3
    c(7) = a(:, :, 1)*b(:, :, 5) + a(:, :, 2)*b(:, :, 4) + a(:, :, 4)*b(:, :, 2) + a(:, :, 5)*b(:, :, 1);             %x^2*y
    c(8) = a(:, :, 2)*b(:, :, 5) + a(:, :, 3)*b(:, :, 4) + a(:, :, 4)*b(:, :, 3) + a(:, :, 5)*b(:, :, 2);             %x*y^2
    c(9) = a(:, :, 3)*b(:, :, 5) + a(:, :, 5)*b(:, :, 3);                                                             %y^3 
    c(10) = a(:, :, 1)*b(:, :, 6) + a(:, :, 6)*b(:, :, 1) + a(:, :, 4)*b(:, :, 4);                                    %x^2    
    c(11) = a(:, :, 2)*b(:, :, 6) + a(:, :, 6)*b(:, :, 2) + a(:, :, 4)*b(:, :, 5) + a(:, :, 5)*b(:, :, 4);            %x*y
    c(12) = a(:, :, 3)*b(:, :, 6) + a(:, :, 6)*b(:, :, 3) + a(:, :, 5)*b(:, :, 5);                                    %y^2
    c(13) = a(:, :, 4)*b(:, :, 6) + a(:, :, 6)*b(:, :, 4);                                                            %x
    c(14) = a(:, :, 5)*b(:, :, 6) + a(:, :, 6)*b(:, :, 5);                                                            %y
    c(15) = a(:, :, 6)*b(:, :, 6);                                                                                    %1
end 