function p = mult_poly42(d, a)
    p = zeros(1, 1, 28);
    p(1) = a(:, :, 1)*d(:, :, 1);                                                                                                                            %x^6
    p(2) = a(:, :, 1)*d(:, :, 2) + a(:, :, 2)*d(:, :, 1);                                                                                                    %x^5*y
    p(3) = a(:, :, 1)*d(:, :, 3) + a(:, :, 2)*d(:, :, 2) + a(:, :, 3)*d(:, :, 1);                                                                            %x^4*y^2
    p(4) = a(:, :, 1)*d(:, :, 4) + a(:, :, 2)*d(:, :, 3) + a(:, :, 3)*d(:, :, 2);                                                                            %x^3*y^3
    p(5) = a(:, :, 1)*d(:, :, 5) + a(:, :, 2)*d(:, :, 4) + a(:, :, 3)*d(:, :, 3);                                                                            %x^2*y^4
    p(6) = a(:, :, 2)*d(:, :, 5) + a(:, :, 3)*d(:, :, 4);                                                                                                    %x*y^5   
    p(7) = a(:, :, 3)*d(:, :, 5);                                                                                                                            %y^6
    p(8) = a(:, :, 4)*d(:, :, 1) + a(:, :, 1)*d(:, :, 6);                                                                                                    %x^5
    p(9) = a(:, :, 4)*d(:, :, 2) + a(:, :, 5)*d(:, :, 1) + a(:, :, 1)*d(:, :, 7) + a(:, :, 2)*d(:, :, 6);                                                    %x^4*y
    p(10) = a(:, :, 4)*d(:, :, 3) + a(:, :, 5)*d(:, :, 2) + a(:, :, 1)*d(:, :, 8) + a(:, :, 2)*d(:, :, 7) + a(:, :, 3)*d(:, :, 6);                           %x^3*y^2
    p(11) = a(:, :, 4)*d(:, :, 4) + a(:, :, 5)*d(:, :, 3) + a(:, :, 1)*d(:, :, 9) + a(:, :, 2)*d(:, :, 8) + a(:, :, 3)*d(:, :, 7);                           %x^2*y^3
    p(12) = a(:, :, 4)*d(:, :, 5) + a(:, :, 5)*d(:, :, 4) + a(:, :, 2)*d(:, :, 9) + a(:, :, 3)*d(:, :, 8);                                                   %x*y^4
    p(13) = a(:, :, 5)*d(:, :, 5) + a(:, :, 3)*d(:, :, 9);                                                                                                   %y^5
    p(14) = a(:, :, 6)*d(:, :, 1) + a(:, :, 4)*d(:, :, 6) + a(:, :, 1)*d(:, :, 10);                                                                          %x^4
    p(15) = a(:, :, 6)*d(:, :, 2) + a(:, :, 4)*d(:, :, 7) + a(:, :, 5)*d(:, :, 6) + a(:, :, 1)*d(:, :, 11) + a(:, :, 2)*d(:, :, 10);                         %x^3*y
    p(16) = a(:, :, 6)*d(:, :, 3) + a(:, :, 4)*d(:, :, 8) + a(:, :, 5)*d(:, :, 7) + a(:, :, 1)*d(:, :, 12) + a(:, :, 2)*d(:, :, 11) + a(:, :, 3)*d(:, :, 10);%x^2*y^2
    p(17) = a(:, :, 6)*d(:, :, 4) + a(:, :, 4)*d(:, :, 9) + a(:, :, 5)*d(:, :, 8) + a(:, :, 2)*d(:, :, 12) + a(:, :, 3)*d(:, :, 11);                         %x*y^3
    p(18) = a(:, :, 6)*d(:, :, 5) + a(:, :, 5)*d(:, :, 9) + a(:, :, 3)*d(:, :, 12);                                                                          %y^4
    p(19) = a(:, :, 6)*d(:, :, 6) + a(:, :, 1)*d(:, :, 13) + a(:, :, 4)*d(:, :, 10);                                                                         %x^3
    p(20) = a(:, :, 6)*d(:, :, 7) + a(:, :, 1)*d(:, :, 14) + a(:, :, 2)*d(:, :, 13) + a(:, :, 4)*d(:, :, 11) + a(:, :, 5)*d(:, :, 10);                       %x^2*y
    p(21) = a(:, :, 6)*d(:, :, 8) + a(:, :, 2)*d(:, :, 14) + a(:, :, 3)*d(:, :, 13) + a(:, :, 4)*d(:, :, 12) + a(:, :, 5)*d(:, :, 11);                       %x*y^2
    p(22) = a(:, :, 6)*d(:, :, 9) + a(:, :, 3)*d(:, :, 14) + a(:, :, 5)*d(:, :, 12);                                                                         %y^3
    p(23) = a(:, :, 1)*d(:, :, 15) + a(:, :, 6)*d(:, :, 10) + a(:, :, 4)*d(:, :, 13);                                                                        %x^2
    p(24) = a(:, :, 2)*d(:, :, 15) + a(:, :, 6)*d(:, :, 11) + a(:, :, 4)*d(:, :, 14) + a(:, :, 5)*d(:, :, 13);                                               %x*y
    p(25) = a(:, :, 3)*d(:, :, 15) + a(:, :, 6)*d(:, :, 12) + a(:, :, 5)*d(:, :, 14);                                                                        %y^2
    p(26) = a(:, :, 4)*d(:, :, 15) + a(:, :, 6)*d(:, :, 13);                                                                                                 %x
    p(27) = a(:, :, 5)*d(:, :, 15) + a(:, :, 6)*d(:, :, 14);                                                                                                 %y
    p(28) = a(:, :, 6)*d(:, :, 15);                                                                                                                          %1
end